svg.script:
	
    	function init(evt){
    		pie=new PieChart();
    		pie.setTitle("救助活动投入统计"); 
				var records = ds.getAllRecords();
				var chartDatas = new Array();
				if(records.length>0) {
					for(var i=0;i<records.length;i++ ) {
						var value = records[i].get("NUM");
						var name = records[i].get("MC")+"（"+value+"元）";
						chartDatas.push({name:name,value:value});
					}
				} else {
					chartDatas.push({name:"未查询到符合条件信息",value:"0"});
				}
				pie.setChartData(chartDatas);
				var colors = new Array("#FF9966","#C2A00E", "#3399CC","#FF6999","#71A214",  "#CC99FF", "#00CC99", "#C2CC0E", "#FFCC66","#71CC14", "#CC68CC", "#66FFCC","#FFFF00");
	    	pie.setColors(colors);
	    	//pie.addEvent("onmouseover","alert('you onmouseover pie')");
	    	//pie.modifyEvent("onmouseover","alert('you modify onmouseover me')");	
	    	//pie.addPieClickEvt("alert('you click me2');");
				pie.show(true,false);
		}
