package com.inspur.cams.comm.comfile.cmd;

import org.loushang.next.web.cmd.BaseAjaxCommand;
import org.loushang.sca.ScaComponentFactory;
import org.loushang.sca.transaction.Trans;

import com.inspur.cams.comm.comphoto.data.ComPhoto;
import com.inspur.cams.comm.comphoto.domain.ComPhotoDomain;
import com.inspur.cams.comm.comphoto.domain.IComPhotoDomain;
import com.inspur.cams.comm.util.IdHelp;
import com.inspur.cams.comm.lob.Blob;

public class ComFileCmd extends BaseAjaxCommand{
	private IComPhotoDomain comPhotoDomain = ScaComponentFactory.getService(ComPhotoDomain.class, "ComPhotoDomain/ComPhotoService");
	@Trans
	public void save(){
		String photoTemp = (String)getParameter("photoTemp");
		ComPhoto comPhoto = new ComPhoto();
		Blob blob = new Blob();
		blob.setData("");
		comPhoto.setPhotoContent(blob);
		comPhoto.setPhotoId(IdHelp.getUUID30());
		getComPhotoDomain().insertPhoto(comPhoto);
		
	}
	
	/**
	 * @param comPhotoDomain the comPhotoDomain to set
	 */
	public void setComPhotoDomain(IComPhotoDomain comPhotoDomain) {
		this.comPhotoDomain = comPhotoDomain;
	}
	/**
	 * @return the comPhotoDomain
	 */
	public IComPhotoDomain getComPhotoDomain() {
		return comPhotoDomain;
	}
	
	

}
